import React from "react";
import { Route } from "react-router-dom";
import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";

import CollectionOverviewWithContainer from "../../componemts/collection-overview/CollectionOverview.container";
import Collections from "../collections/Collections";
import {
  seletIsFetching,
  selectISCollectionLoaded,
} from "../../redux/shop/shop.selector";
import { fetchCollectionStartAsync } from "../../redux/shop/shop.action";
import WithSpinner from "../../componemts/with-spinner/WithSpinner";

const CollectionsWithSpinner = WithSpinner(Collections);

class Shop extends React.Component {
  componentDidMount() {
    const { fetchCollectionStartAsync, isCollectionLoaded } = this.props;
    console.log(isCollectionLoaded);
    !isCollectionLoaded && fetchCollectionStartAsync();
  }

  render() {
    const { match, isFetching, isCollectionLoaded } = this.props;
    return (
      <div>
        <Route
          exact
          path={`${match.path}`}
          render={(props) => (
            <CollectionOverviewWithSpinner isLoading={isFetching} {...props} />
          )}
        />
        <Route
          path={`${match.path}/:collectionId`}
          render={(props) => (
            <CollectionsWithSpinner
              isLoading={!isCollectionLoaded}
              {...props}
            />
          )}
        />
      </div>
    );
  }
}
const mapStateToProp = createStructuredSelector({
  isFetching: seletIsFetching,
  isCollectionLoaded: selectISCollectionLoaded,
});

export default connect(mapStateToProp, { fetchCollectionStartAsync })(Shop);
