import userActionType from "./user.type";

export const setCurrentUser = (user) => ({
  type: userActionType.SET_CURRENT_USER,
  payload: user,
});

export const googleSignInStart = () => ({
  type: userActionType.GOOGLE_SIGN_IN_START,
});

export const googleSignInSuccess = (user) => ({
  type: userActionType.GOOGLE_SIGN_IN_SUCCESS,
  payload: user,
});

export const googleSignInFailure = (errorMessage) => ({
  type: userActionType.GOOGLE_SIGN_IN_FAILURE,
  payload: errorMessage,
});

export const emailSignInFailure = (errorMessage) => ({
  type: userActionType.EAMIL_SIGN_IN_FAILURE,
  payload: errorMessage,
});

export const emailSignInStart = (emailAndPassword) => ({
  type: userActionType.EMAIL_SIGN_IN_START,
  payload: emailAndPassword,
});

export const emailSignInSuccess = (user) => ({
  type: userActionType.EMAIL_SIGN_IN_SUCCESS,
  payload: user,
});

export const userSignoutStart = () => {
  type: userActionType.USER_SIGN_OUT_START;
};
export const userSignoutSuccess = () => {
  type: userActionType.USER_SIGN_OUT_SUCCESS;
};
export const userSignoutFailure = (error) => {
  type: userActionType.USER_SIGN_OUT_FAILURE;
  payload: error;
};
