import { takeLatest, put, all, call } from "redux-saga/effects";

import userActionType from "./user.type";
import {
  signInFailure,
  signInSuccess,
  userSignoutSuccess,
  userSignoutFailure,
} from "./user.action";
import {
  auth,
  googleProvider,
  createUserProfileDocument,
} from "../../firebase/firebase.utils";

export function* googleSignInStart() {
  try {
    console.log("user saga get called");
    const { user } = yield auth.signInWithPopup(googleProvider);
    const userRef = yield createUserProfileDocument(user);
    const userSnapshot = yield userRef.get();
    yield put(
      googleSignInSuccess({ id: userSnapshot.id, ...userSnapshot.data() })
    );
  } catch (error) {
    console.log(error);
    yield put(googleSignInFailure(error.message));
  }
}

export function* onGoogleSignInStart() {
  yield takeLatest(userActionType.GOOGLE_SIGN_IN_START, googleSignInStart);
}

export function* emailSignInStart({ payload: { email, password } }) {
  try {
    console.log("yess");
    const { user } = yield auth.signInWithEmailAndPassword(email, password);
    const userRef = yield createUserProfileDocument(user);
    const userSnapshot = yield userRef.get();
    yield put(
      emailSignInSuccess({ id: userSnapshot.id, ...userSnapshot.data() })
    );
  } catch (error) {
    console.log(error);
    yield put(emailSignInFailure(error));
  }
}

export function* onEmailSignInStart() {
  yield takeLatest(userActionType.EMAIL_SIGN_IN_START, emailSignInStart);
}

export function* userSignOut() {
  try {
    yield auth.signOut();
    yield put(userSignoutSuccess());
  } catch (error) {
    yield put(userSignoutFailure(error.message));
  }
}

export function* onUserSignOut() {
  yield takeLatest(userActionType.USER_SIGN_OUT_START, userSignOut);
}

export function* userSaga() {
  yield all([
    call(onGoogleSignInStart),
    call(onUserSignOut),
    call(onEmailSignInStart),
  ]);
}
