import { shopActionType } from "./shop.type";

const INITAIL_STATE = {
  collections: {},
};

const shopReducer = (state = INITAIL_STATE, action) => {
  switch (action.type) {
    case shopActionType.UPDATE_COLLECTION_OBJECT:
      return { ...state, collections: action.payload };
    default:
      return state;
  }
};

export default shopReducer;
