import { takeLatest, call, put, all, select } from "redux-saga/effects";
import { shopActionType } from "./shop.type";
import { fetchCollectionSuccess, fetchCollectionFailure } from "./shop.action";
import {
  firestore,
  convertCollectionSnapshotToMap,
} from "../../firebase/firebase.utils";
import { selectColletionsPerview } from "./shop.selector";
export function* fetchCollectionsAsync() {
  try {
    const collectionRef = firestore.collection("collection");
    const snapshot = yield collectionRef.get();
    const collectionObject = yield call(
      convertCollectionSnapshotToMap,
      snapshot
    );
    yield put(fetchCollectionSuccess(collectionObject));
  } catch (error) {
    yield put(fetchCollectionFailure(error.message));
  }
}

export function* fetchCollectionStart() {
  yield takeLatest(
    shopActionType.FETCH_COLLECTION_START,
    fetchCollectionsAsync
  );
}

export function* collectionsCheck() {
  const collections = select(selectColletionsPerview);
  console.log(collections);
}

export function* onCollectionsCheck() {
  yield takeLatest(shopActionType.CHECK_COLLECTION, collectionsCheck);
}

export function* shopsagas() {
  yield all([call(fetchCollectionStart), onCollectionsCheck]);
}
