import { cartActionType } from "./cart.type";

const INITIAL_STATE = {
  hidden: true,
};

const cartReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case cartActionType.HIDE_DROPDOWN:
      return {
        ...state,
        hideDropDown: ![hidden],
      };
    default:
      return state;
  }
};

export default cartReducer;
