import { cartActionType } from "./cart.type";
import { addToCartItem } from "./cart.util";
const INITIAL_STATE = {
  hidden: false,
  cartItems: [],
};

const cartReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case cartActionType.HIDE_DROPDOWN:
      return {
        ...state,
        hidden: !state.hidden,
      };
    case cartActionType.ADD_ITEM:
      return {
        ...state,
        cartItem: addToCartItem(state.cartItems, action.payload),
      };
    default:
      return state;
  }
};

export default cartReducer;
