import React from "react";
import { Route } from "react-router-dom";
import { connect } from "react-redux";

import CollectionOverview from "../../componemts/collection-overview/CollectionOverview";
import Collections from "../collections/Collections";
import { updateShopCollections } from "../../redux/shop/shop.action";
import {
  firestore,
  convertCollectionSnapshotToMap,
} from "../../firebase/firebase.utils";
import WithSpinner from "../../componemts/with-spinner/WithSpinner";

const CollectionOverviewWithSpinner = WithSpinner(CollectionOverview);

const CollectionsWithSpinner = WithSpinner(Collections);

class Shop extends React.Component {
  unsubcribedCollection = null;

  componentDidMount() {
    const { updateShopCollections } = this.props;

    const collectionRef = firestore.collection("collection");
    collectionRef.onSnapshot((snapshot) => {
      const collectionObject = convertCollectionSnapshotToMap(snapshot);
      updateShopCollections(collectionObject);
    });
  }

  render() {
    const { match } = this.props;
    return (
      <div>
        <Route exact path={`${match.path}`} component={CollectionOverview} />
        <Route path={`${match.path}/:collectionId`} component={Collections} />
      </div>
    );
  }
}

export default connect(null, { updateShopCollections })(Shop);
