import { takeEvery, call, put } from "redux-saga/effects";
import { shopActionType } from "./shop.type";
import { fetchCollectionSuccess, fetchCollectionFailure } from "./shop.action";
import {
  firestore,
  convertCollectionSnapshotToMap,
} from "../../firebase/firebase.utils";

export function* fetchCollectionsAsync() {
  try {
    const collectionRef = firestore.collection("collection");
    const snapshot = yield collectionRef.get();
    const collectionObject = yield call(
      convertCollectionSnapshotToMap,
      snapshot
    );
    put(fetchCollectionSuccess(collectionObject));
  } catch (error) {
    put(fetchCollectionFailure(error.message));
  }
}

export function* fetchCollectionStart() {
  yield takeEvery(shopActionType.FETCH_COLLECTION_START, fetchCollectionsAsync);
}
