import React from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";

import { hideDropDown } from "../../redux/cart/cart.action";
import { ReactComponent as Logo } from "../../assets/crown.svg";
import CartItem from "../cart-item/CartItem";
import CartDropdown from "../cart-dropdown/CartDropdown";
import { auth } from "../../firebase/firebase.utils";
import "./Header.styles.scss";

function Header({ user, showDropDown, hideDropDown }) {
  return (
    <div className="header">
      <Link to="/" className="logo-container">
        <Logo className="logo" />
      </Link>
      <div className="options">
        <Link className="option" to="/shop">
          SHOP
        </Link>
        <Link className="option" to="/contact">
          CONTACT
        </Link>
        {user ? (
          <div className="option" onClick={() => auth.signOut()}>
            Sign out
          </div>
        ) : (
          <Link className="option" to="/signin">
            SIGN IN
          </Link>
        )}
        <div
          className=""
          onClick={() => {
            console.log("CLICK!!");
            hideDropDown(showDropDown);
          }}
        >
          <CartItem />
        </div>
      </div>
      {showDropDown && <CartDropdown />}
    </div>
  );
}
const mapStateToProps = (state) => ({
  user: state.user.currentUser,
  showDropDown: state.cart.hideDropDown,
});

export default connect(mapStateToProps, { hideDropDown })(Header);
