import { takeLatest, put, all, call } from "redux-saga/effects";

import userActionType from "./user.type";
import { googleSignInFailure, googleSignInSuccess } from "./user.action";
import { auth, googleProvider } from "../../firebase/firebase.utils";

export function* googleSignInStart() {
  try {
    console.log("user saga get called");
    const userRef = yield auth.signInWithPopup(googleProvider);
    put(userActionType.GOOGLE_SIGN_IN_SUCCESS);
  } catch (error) {
    console.log(error);
    put(googleSignInFailure(error.message));
  }
}

export function* onGoogleSignInStart() {
  yield takeLatest(userActionType.GOOGLE_SIGN_IN_START, googleSignInStart);
}

export function* userSaga() {
  yield all([call(onGoogleSignInStart)]);
}
